import React from 'react';
import './App.css';

export default class RandomGenerate extends React.Component {

    state = {
      topValue: "Enter the top TEXT",
      bottomValue: "Enter the bottom TEXT",
    }

    handleTopChange = e => {
      this.setState ({
        topValue: e.target.value,
      });
    };
    handleBottomChange = e => {
      this.setState ({
        bottomValue: e.target.value,
      });
    };

  render () {

    const renderFull = (
      <div>
        <div className="header">
        <input type = "string" className = "input-top" value = {this.state.topValue}  onChange = {this.handleTopChange} />
        <input type = "string" className = "input-bottom" value = {this.state.bottomValue} onChange = {this.handleBottomChange} />
        <input type = "number" className = "input-font-size" value = {this.state.fontValue}  onChange = {this.handleFontChange} />
        <button type = "button" id = "random-generate"> Random </button>
        </div>
        <div className = "slider">
          <div className = "top-text-container">
            <span className = "top-text" > {this.state.topValue} </span>
          </div>
          <div className = "photo">
            <img src = "https://images.unsplash.com/photo-1566728618788-11b888adb6b6?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1312&q=80" />
            {/* <img src = "https://images.unsplash.com/photo-1562184167-2b35a31ab178?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1351&q=80" />
            <img src = "https://images.unsplash.com/photo-1566640241442-bd59e5f70d08?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80" />
            <img src = "https://images.unsplash.com/photo-1566681990869-2ab77a7b6461?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80" />
            <img src = "https://images.unsplash.com/photo-1566793474285-2decf0fc182a?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80" />  */}
          </div>
          <div className = "bottom-text-container">
          <span className = "top-text" > {this.state.bottomValue} </span>
          </div>
        </div>
      </div>
    );

    return(
      <div className="full-page">
       {renderFull}
      </div>
    );
  }
}
