import React from 'react';
import './App.css';


export default class RandomGenerate extends React.Component {

    myArray = [
      {
        id: 1,
        src: "https://images.unsplash.com/photo-1566728618788-11b888adb6b6?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1312&q=80",
      },
      {
        id: 2,
        src: "https://images.unsplash.com/photo-1562184167-2b35a31ab178?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1351&q=80",
      },
      {
        id: 3,
        src: "https://images.unsplash.com/photo-1566640241442-bd59e5f70d08?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
      },
      {
        id: 4,
        src: "https://images.unsplash.com/photo-1566681990869-2ab77a7b6461?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80",
      },
      {
        id:5,
        src: "https://images.unsplash.com/photo-1566793474285-2decf0fc182a?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80",
      },
    ]

    state = {
      topValue: "Enter the top TEXT",
      bottomValue: "Enter the bottom TEXT",
      fontValue : 20,
    }

    handleTopChange = e => {

      this.setState ({
        topValue: e.target.value,
      });
    };

    handleBottomChange = e => {
      this.setState ({
        bottomValue: e.target.value,
      });
    };

    handleFontChange = e => {
      this.setState ({
        fontValue: e.target.value,
      });
    };






  render () {

    const spanStyle = {
      fontSize: this.state.fontValue +"px",
    };

    const renderFull = (
      <div className="page">
        <div className="header">
        <input type = "string" className = "input-top" value = {this.state.topValue}  onChange = {this.handleTopChange} />
        <input type = "string" className = "input-bottom" value = {this.state.bottomValue} onChange = {this.handleBottomChange} />
        <input type = "number" className = "input-font-size" value = {this.state.fontValue}  onChange = {this.handleFontChange} />
        <button type = "button" id = "random-generate" onClick={this.renderPhoto} > Random </button>
        </div>
        <div className = "slider">
          <div className = "top-text-container" >

            <span className = "top-text" style={spanStyle} > {this.state.topValue} </span>
          </div>
          <div className = "photo">
            <img src ={this.renderPhoto} />
          </div>
          <div className = "bottom-text-container">
          <span className = "bottom-text" style={spanStyle} > {this.state.bottomValue} </span>
          </div>
        </div>
      </div>
    );

    return(
      <div className="full-page" >
       {renderFull}
      </div>
    );
  }
}
