import React from 'react';
import './App.css';


export default class RandomGenerate extends React.Component {

    myArray = [
      {
        id: 1,
        src: "https://images.unsplash.com/photo-1566728618788-11b888adb6b6?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1312&q=80",
      },
      {
        id: 2,
        src: "https://images.unsplash.com/photo-1562184167-2b35a31ab178?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1351&q=80",
      },
      {
        id: 3,
        src: "https://images.unsplash.com/photo-1566640241442-bd59e5f70d08?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
      },
      {
        id: 4,
        src: "https://images.unsplash.com/photo-1566681990869-2ab77a7b6461?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80",
      },
      {
        id:5,
        src: "https://images.unsplash.com/photo-1566793474285-2decf0fc182a?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80",
      },
    ]

    state = {
      topValue: "",
      bottomValue: "",
      fontValue: 20,
      id: 1,
      stateArray: [],
    }

    componentDidMount() {
      fetch('https://api.imgflip.com/get_memes')
        .then(function(response) {
        return response.json();
      })
      .then(myJson => {
        let dataArray = JSON.stringify(myJson.data.memes);      

        this.setState({
          stateArray: dataArray,
        })

        console.log(this.state.stateArray);

      });

    }


    setValue = (value, key) => {
      this.setState({
        [key]: value,
      });
    };

   renderPhoto = () => {
      let randomNumber = Number(Math.floor(Math.random()*5));
      let photos = this.myArray;

      for(let i = 0 ; i < photos.length; i++) {
        if(randomNumber === photos[i].id) {
          this.setState ({
            id: photos[i].id,
          });
        }
      }
    };

  render () {

    const spanStyle = {
      fontSize: this.state.fontValue +"px",
    };

    const renderFull = (
      <div className="page">
        <div className="header">
        <input type="string" placeholder="Please enter top text" className="input-top" value={this.state.topValue}  onChange={(e) => this.setValue(e.target.value, "topValue")} />
        <input type="string" placeholder="Please enter bottom text" className="input-bottom" value={this.state.bottomValue} onChange={(e) => this.setValue(e.target.value, "bottomValue")} />
        <input type="number" placeholder="Please enter font size" className="input-font-size" value={this.state.fontValue}  onChange={(e) => this.setValue(e.target.value, "fontValue")} />
        <button type="button" id="random-generate" onClick={this.renderPhoto} > Random </button>
        </div>
        <div className= "slider">
          <div className="top-text-container" >
            <span className="top-text" style={spanStyle} > {this.state.topValue} </span>
          </div>
          <div className="photo">
            <img src={this.myArray[this.state.id].src} />
          </div>
          <div className="bottom-text-container">
          <span className="bottom-text" style={spanStyle} > {this.state.bottomValue} </span>
          </div>
        </div>
      </div>
    );

    return(
      <div className="full-page" >
       {renderFull}
      </div>
    );
  }
}
